from docx import Document
from docx.oxml import parse_xml

def insert_equation(doc_path, omml_xml, output_doc_path):
    doc = Document(doc_path)

    equation_xml = f'<w:p xmlns:w="http://schemas.openxmlformats.org/wordprocessingml/2006/main" \
                    xmlns:m="http://schemas.openxmlformats.org/officeDocument/2006/math">{omml_xml}</w:p>'
    
    eq_element = parse_xml(equation_xml)
    doc._body._element.append(eq_element)

    doc.save(output_doc_path)

